$(document).ready(function(){
    // Array of quotes
    var quotes = [
        // Add your quotes here
    ];

    // Function to display quotes
    function displayQuote() {
        var randomIndex = Math.floor(Math.random() * quotes.length);
        var quote = quotes[randomIndex];
        $('#quote').css('opacity', '0'); // Set initial opacity to 0
        $('#quote').text(quote).animate({ opacity: 1 }, 1000); // Fade in the quote with animation
    }

    // Initial call to display a quote
    displayQuote();

    // Function to change quote every 5 seconds
    setInterval(displayQuote, 5000);
});
